%Plot the spectrogram for Phase Differencefunction PlotPhaseSpectrogram(S,F,T,titleText)%Plot the Spectrogramsurf(T,F,S.','edgecolor','flat'); axis tight; view(0,90);colormap(hsv);col=colorbar;ylabel(col,'Angle/Frequency (rad/Hz)','Interpreter','latex')title(titleText,'Interpreter','latex')xlabel('Time (s)','Interpreter','latex')ylabel('Frequency (Hz)','Interpreter','latex')end