function PlotSpectrogram(P,F,T,titleText,caxislim)surf(T,F,10*log10(P.'),'edgecolor','flat'); axis tight; view(0,90);colormap(flipud(gray));col=colorbar;if nargin == 5    caxis(caxislim);endylabel(col,'Power/Frequency (dB/Hz)','Interpreter','latex')title(titleText,'Interpreter','latex')xlabel('Time (s)','Interpreter','latex')ylabel('Frequency (Hz)','Interpreter','latex')end